import { useEffect, useState } from 'react';
import styles from './TaskList.module.css';


function TaskList(props) {

  localStorage.setItem(props.task , props.des);


  const store = [];

  for(let i = 0; i <localStorage.length;i++){
    // console.log(localStorage.getItem(localStorage.key(i)));
       store.unshift({
         id: localStorage.key(i),
         des:localStorage.getItem(localStorage.key(i))
       });
  }

  console.log(store[0].id);


 const [background , setBackground] = useState(styles.section);
 const [active , setActive] = useState(styles.com);
const [onDel , setOndel] = useState(styles.del);
 const onCompleteHandler = () => {
   setBackground(styles.completeSection); 
   setActive(styles.comDisabled);
  }



 return (
   
   <div className= {styles.div}>
   <ul className = {styles.ul}>
    {store.map((task) => {
       return(
      <li  className ={styles.list}  key = {task.id}>
         <section className = {background}>
      <div className = {styles.heder}>
        <h3> Task No:{task.id} </h3>
      </div>
      <div className = {styles.task}>
        <h2> {task.des} </h2>
      </div>

      <div className = {styles.btn}>
        <button className =  {onDel} type="button">Delete</button>
        <button className =  {active} onClick ={onCompleteHandler} type='submit'>Complete</button>
      </div>
    </section>
     </li>
  )
  
})}
 </ul>
 </div>
);
}

export default TaskList;
