// Writing First Components//

// Adding Css File //

import './ExpenseItem.css'
import ExpenseDate from './ExpenseDate.js';
import Card from '../UI/Card';

// to change state of an element import {useState}//
import {useState} from 'react';
// TO pass Data Between React component Use props // 
// Data passed to Expense Item from App component Array//


function ExpenseItem(props) {
    
    // Adding JS to add Data Dynamically into Components //
  //   const expenseDate = new Date(2021,4,21);
  //   const expenseTitle = "Car Insurance";
  //   const expenseAmount = 294.67;
  
      // Use { } to include // 
  // console.log(props);

// useState take array with 2 elements//

let[title,setTitle] = useState(props.title);
//usestate function takes (props.title)
const clickHandler = () =>{
    setTitle('Updated'); 
    
}
    return (
        <Card className = "expense-item" >

             {/* Data added dynamically using props parameter// */}

          <ExpenseDate date={props.date}/>
            <div className = "expense-item__description">
                <h2> {title} </h2>
                <div className = "expense-item__price"> ${props.amount} </div>
            </div>
            {/* Adding Event Listeners On click takes a function as Input */}
            <button onClick = {clickHandler}>Change Title!</button>
        </Card>
    )
}

export default ExpenseItem;
