import TaskList from "./TaskList";

function Storage(props){

    const store = [];

    store.push({
        id: props.task,
        taskNo: props.task,
        des: props.des,
    });

    for(let data of store){
        localStorage.setItem('task' , store);
    }

    for(let data of store){
        localStorage.getItem('task');
        console.log(data);
           
        const taskNo = data.taskNo;
        const des = data.des;

        console.log(taskNo.length , des);
        <TaskList no = {taskNo} des = {des} />
    }
  
};

export default Storage;
