  {
    const details = data.results.map(movieData =>{
      return {
        id : movieData.episode_id,
        title : movieData.title,
        openingText : movieData.ope
      }
    })
    setMovies(data.results);
  })

}


  return (
    <React.Fragment>
      <section>
        <button onClick = {fetchMovieHandler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies= {movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
