import React, { useState } from 'react';
import Delete from './Delete';
import styles from './TaskList.module.css';


function TaskList(props) {

  localStorage.setItem(props.task , props.des);

  const store = [];

  for(let i = 0; i <localStorage.length;i++){
    // console.log(localStorage.getItem(localStorage.key(i)));
       store.unshift({
         id: localStorage.key(i),
         des:localStorage.getItem(localStorage.key(i))
       });
  }

//  const [background , setBackground] = useState(styles.section);

const completed = [];

const [active , setActive] = useState(styles.com);
const [onDel , setOndel] = useState(styles.del);
const [complete , setIsComplete] = useState('');
const [array , setArray] = useState(completed);

const onCompleteHandler = (event) => {
  
  
  let ids = event.currentTarget.id;

  completed.push(ids);

  setArray((arr) => [...arr] )

console.log(`Length : ${completed.length}`);

  for(let i = 0; i < completed.length; i++){
    setIsComplete(completed[i]);
  }
};

let [del ,setDel] = useState(false);

 
  const onDeleteHandler = () => {
      setDel(!del);
  const key = localStorage.key(1);
  console.log(key);
  };

 
 return (
   <React.Fragment>
   <div className= {styles.div}>
   <ul className = {styles.ul}>
    {store.map((task , i) => {
       return(
         <li  className ={styles.list}  key = {task.id}>
        <section className = {`${task.id === complete ? styles.completeSection : styles.section }`}>
      <div className = {styles.heder}>
        <h3> Task No:{task.id} </h3>
      </div>
      <div className = {styles.task}>
        <h2> {task.des} </h2>
      </div>

      <div className = {styles.btn}>
        <button className =  {onDel}  onClick = {onDeleteHandler} type="submit">Delete</button>
        <button className =  {active} id = {task.id} onClick ={onCompleteHandler} type='submit'>Complete</button>
      </div>
    </section>
     </li>
  )
  
})}
 </ul>
 </div>
 {del && <Delete onDel = {del} store = {store} /> }
</React.Fragment>
); 

}

export default TaskList;
