import styles from './TaskList.module.css';


function TaskList(props) {

 
  const store = [];


  store.push({
      id: props.task,
      taskNo: props.task,
      des: props.des,
  });

  console
  for(let data of store){
    localStorage.setItem(props.task  , JSON.stringify(store));
      console.log(data);
  }


  const data = JSON.parse(localStorage.getItem(props.task));

  // console.log(data);
 



  return (
             

       <ul>
      <li  key = '1'>
         <section className = {styles.section}>
      <div className = {styles.heder}>
        <h3> Task No: </h3>
      </div>
      <div className = {styles.task}>
        <h2> 2 </h2>
      </div>

      <div className = {styles.btn}>
        <button className =  {styles.del} type="button">Delete</button>
        <button className =  {styles.com} type="button">Complete</button>
      </div>
    </section>
    </li>
    </ul>
  
    
  );
}

export default TaskList;
