import TaskList from "./TaskList";

function Storage(props){

  localStorage.setItem('id' , props.task);
 localStorage.setItem('no' , props.task);
 localStorage.setItem('des' , props.des);

  console.log(localStorage.getItem('id'));
  console.log(localStorage.getItem('des'));

  const id = localStorage.getItem('id');
  const no = localStorage.getItem('id');
  const des = localStorage.getItem('des');

  console.log(id , no , des);

  const store = [];
store.length()
  store.push({
      id : id,
      no : no,
      des : des,
  });

  <TaskList  data = {store} />
};

export default Storage;
