          console.log('clicked'); 
          setNo(!no);
          props.setDel(false); 
        }

  let id = props.id;
  ;

    const yesBtnHandler = useCallback(( id) => {
        console.log(id)      
     console.log('Dlete.js :' + props.id);
      setDelted(deleted.filter(task => task.id !== id));
//      localStorage.removeItem(props.id);
//   console.log(deleted);

 
     setNo(!no);

    },[]);

    return(
    <div className = {`${no === false ? styles.del :styles.delOn}`}>
            <section>
                <div>
                    <h3>Are you Sure ?</h3>
                </div>
                <div>
                    <button type = 'button' onClick = {() => yesBtnHandler(props.id)}> Yes </button>
                    <button type = 'button'   onClick = {noBtnHandler}> No </button>
                </div>
            </section>
        </div>
   );
}

export default Delete;
