// Useref or Use State to get Input from User//
import { useEffect, useRef} from "react";
import { useState} from "react";

const SimpleInput = (props) => {

 const [userName,setUserName] = useState('');
 const [isValid,isNotValid] = useState(false);
 const [isEntered,isNotEntered] = useState(false);

 useEffect


  const Inputs = useRef();

  const userNameHandler = (event)=>{

    setUserName (event.target.value);
  }

  const formInputHandler = (event)=>{

    event.preventDefault();


    const userInput = Inputs.current.value;

    if(userInput.trim() === ''){
      isNotValid(true);
      return; 
    }

    isNotValid(false);

   console.log(userName);
    console.log(userInput);
  }

  const formClasses = isValid ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit = {formInputHandler}>
      <div className={formClasses}>
        <label htmlFor='name'>Your Name</label>
        <input ref = {Inputs}  type='text' onChange = {userNameHandler} value = {userName}  id='name' />
      </div>
      {isValid && <p className = "error-text"> Enter a Valid Name...</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
