import React, { useState } from 'react';
import styles from './Delete.module.css';

function Delete (props){
    
    //  console.log();

    const store = props.store;
    const [no , setNo] = useState(false);
    const [deleted ,setDelted] = useState(store);


   console.log('Length :' + store.length);
    const noBtnHandler = () => {
          console.log('clicked'); 
          setNo(!no);
          props.setDel(false); 
        }


    const yesBtnHandler = (e) => {
        e.preventDefault();
     console.log('Dlete.js :' + props.id);
        

     let updatedStore = deleted.filter((task) => task.id !== props.id);
     console.log()
         localStorage.removeItem(props.id); 
     setDelted([...updatedStore]);
   
     setNo(!no);

     console.log('Length Again :' + store.length);

    };

    return(
    <div className = {`${no === false ? styles.del :styles.delOn}`}>
            <section>
                <div>
                    <h3>Are you Sure ?</h3>
                </div>
                <div>
                    <button type = 'button' onClick = {yesBtnHandler}> Yes </button>
                    <button type = 'button'   onClick = {noBtnHandler}> No </button>
                </div>
            </section>
        </div>
   );
}

export default Delete;
