        // });

        // OR 3 Way //
        //   setUserInput((previousState) =>{
        //     return {...previousState, enteredTitle:event.target.value};
        //   });
    }
         //Amount//

    const amountChangeHandler = (event) => {
           // Multiple States //

        setEnteredAmount(event.target.value);
        
        //Alternate Way//
        // setUserInput({
        //     ...userInput,
        //     enteredAmount: event.target.value,
        // });

        // OR 3 Way //
     //   setUserInput((previousState)=>{
    //       return {...previousState, enteredAmount : event.target.value};
    //   });
    };

       //Date//

    const dateChangeHandler = (event) => {
           // Multiple States //
           setEnteredDate(event.target.value);
       
        //Alternate Way//
    //     setUserInput({
    //        ...userInput,
    //        enteredDate: event.target.value,
    //    })

       // Or 3 Way//
    // setUserInput((previousState)=>{
    //     return{...previousState,enteredDate : event.target.value};
     // });
    };

    const submitHandler = (event)=>{
       event.preventDefault();
       // using object to store submitted values //
       
       const expenseData = {
         
        title : enteredTitle,
        amount 

       }
       enteredTitle = event.target.value;
       enteredAmount = event.target.value;
       enteredDate = event.target.value 
    }

    return (
        <form onSubmit = {submitHandler}>
            <div className='new-expense__controls'>

                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler}></input>
                </div>


                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type="number" onChange={amountChangeHandler} ></input>
                </div>


                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type="date" min="2019-01-01" onChange={dateChangeHandler}></input>

                </div>

                <button type="submit" className="new-expense__actions">Add Expense</button>

            </div>


        </form>



    )


}

export default ExpenseForm;
