// Useref or Use State to get Input from User//
import { useEffect, useRef} from "react";
import { useState} from "react";

const SimpleInput = (props) => {

 const [userName,setUserName] = useState('');
 const [isValid,isNotValid] = useState(false);
 const [isEntered,isNotEntered] = useState(false);

 const [userEmail,setUserEmail] = useState('');
 const [emailEntered,emailNotEntered] = useState(false);

 let formIsValid = false;

 if(isValid && userName){
   formIsValid = true;
 }

 useEffect(()=>{
if(isValid){
  console.log('Enter a Valid Value');
}
 },[isValid]);


  const Inputs = useRef();

  const userNameHandler = (event)=>{

    setUserName (event.target.value);

    if(event.target.value.trim() !== ''){
      isNotValid(false);

    }

  }

  const userEmailHandler = (event)=> {
    setUserEmail (event.target.value);

    if(!event.target.value.trim().includes('@')){
        isNotValid(true);
    }
  }

  const onBlurHandler = (event)=>{
    isNotEntered(true);

    if(userName.trim() === ''){
      isNotValid(true);

    }

  }

  const emailBlurHandler = () => {
    emailNotEntered(true);

    if(userEmail.trim() === ''){
      isNotValid(true);
    }
  }

  const formInputHandler = (event)=>{

    event.preventDefault();
    const userInput = Inputs.current.value;



    if(userInput.trim() === ''){
      isNotEntered(true);
      isNotValid(true);
      return; 
    }

    isNotValid(false);

   console.log(userName);
    console.log(userInput);
  }



  const isOk = isValid && isEntered;
  const emailOk = isValid 

  const formClasses = !isOk ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit = {formInputHandler}>
      <div className={formClasses}>
        <label htmlFor='name'>Your Name</label>
        <input ref = {Inputs}  type='text' onChange = {userNameHandler} onBlur = {onBlurHandler} value = {userName}  id='name' />
      </div>
      {isOk && <p className = "error-text"> Enter a Valid Name...</p>}

      <div className={formClasses}>
        <label htmlFor='email'>Your Email</label>
        <input ref = {Inputs}  type= 'email' onChange = {userEmailHandler} onBlur = {emailBlurHandler} value = {userEmail}  id='email' />
      </div>
      {isOk && <p className = "error-text"> Enter a Valid Email...</p>}
      <div className="form-actions">
        <button disabled = {formIsValid} >Submit</button>
      </div>

    </form>
  );
};

export default SimpleInput;
