import TaskList from "./TaskList";

function Storage(props){

    const store = [];

    store.push({
        id : props.task,
        taskNo : props.task,
        des : props.des,
    })


  localStorage.setItem('s' , props.task);
 
//   <TaskList  data = {des} />
};

export default Storage;
