import styles from './Form.module.css';
import {useRef} from 'react';
import Storage from './storage';

const style = `${styles.fields} ${styles.req}`;

function TaskForm(){

    const noRef = useRef('');  

    const inputRef = useRef(''); 

    const submitHandler = (event) => {
      event.preventDefault();
    };

    let taskNo = noRef;
    let taskDes = '';

    const btnHandler = () => {
       taskNo = noRef.current.value;
       taskDes = inputRef.current.value; 

      console.log(  taskNo , taskDes);
    }

    return(
       
       <section className = {styles.section}>
           <h3>Enter A Task</h3>
       <div className = {styles.form}>
      <form  onSubmit = {submitHandler} >
        <div className = {style} >
          <label htmlFor='no'>Task - No:</label>
          <input type = 'number' id ='no' ref = {noRef}></input>
          </div>

          <div className = {styles.fields} >
          <label htmlFor = 'task'> Task:</label>
          <input type ='text' id = 'task' ref = {inputRef}></input>
          </div>

          <div className = {styles.btn}>
          <button type = 'submit' onClick = {btnHandler}> Add </button>  
          </div>
      

      </form>
      </div>
         
      <Storage task = {taskNo} des = {taskDes} />
      </section>
      
    );

};

export default TaskForm;
