import { useState } from "react";
import useInput from "./use-Input";

function useUserInput(validate) {

    // takes 2 States //

    //1 . Check if user has entered a value //

    const[enterValue,setEnterValue] = useState('');

    //2. CHeck if user entered a Input //

    const [enterInput,setEnterInput] = useState(false);

    // enter value by user //
    const isValid = validate(enterValue);

    const hasError = !isValid && enterInput;

    const valueChangeHandler = (event) => {
        setEnterValue(event.target.value);
    }

    const inputBlurHandler = ()=>{
        setEnterInput(true);
    }

    const reset =()=>{
        setEnterValue('');
        setEnterInput(false);
    };

    return {
        value : enterValue,
        isValid : isValid,
        hasError,
        valueChangeHandler,
        inputBlurHandler,
        reset
    };

};

export default useUserInput;
