import useUserInput from "./hooks/useInput";



const BasicForm = (props) => {

  const{
  value : firstNameValue,
  isValid : firstNameIsValid,
  valueChangeHandler : firstNameChangeHandler,
  inputBlurHandler : firstNameBlurHandler,
  reset : resetFirstName 

  } = useUserInput(value => value.trim() !== '');

  const{
    value : lastNameValue,
    isValid : lastNameIsValid,
    valueChangeHandler : lastNameChangeHandler,
    inputBlurHandler : lastNameBlurHandler,
    reset : resetLastName 

    } = useUserInput(value => value.trim() !== '');


  const{
    value : emailValue,
    isValid : emailIsValid,
    valueChangeHandler : emailChangeHandler,
    inputBlurHandler : emailBlurHandler,
    reset : resetemail 

    } = useUserInput(value => value.trim().includes('@'));


  return (
    <form>
      <div className='control-group'>
        <div className='form-control'>
          <label  htmlFor='name'>First Name</label>
          <input  type='text' value = {firstNameValue} onChange ={firstNameChangeHandler} onBlur = {firstNameBlurHandler} id='name' />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' value = {lastNameValue} onChange = {lastNameChangeHandler} id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
