import styles from './Form.module.css';
import {useRef, useState} from 'react';
import Storage from './storage';
import TaskList from './TaskList';

const style = `${styles.fields} ${styles.req}`;

function TaskForm(){

    const noRef = useRef('');  

    const inputRef = useRef(''); 
  
    const [input , setInput] = useState('');
    const [task , setTask] = useState('');
    const submitHandler = (event) => {
      event.preventDefault();
    };

    
    const btnHandler = () => {
    
      let taskNo = noRef.current.value;
      setInput (taskNo);

      let taskDes = inputRef.current.value; 
         setTask(taskDes);


    }

    return(
       
       <section className = {styles.section}>
           <h3>Enter A Task</h3>
       <div className = {styles.form}>
      <form  onSubmit = {submitHandler} >
        <div className = {style} >
          <label htmlFor='no'>Task - No:</label>
          <input type = 'number' id ='no' ref = {noRef}></input>
          </div>

          <div className = {styles.fields} >
          <label htmlFor = 'task'> Task:</label>
          <input type ='text' id = 'task' ref = {inputRef}></input>
          </div>

          <div className = {styles.btn}>
          <button type = 'submit' onClick = {btnHandler}> Add </button>  
          </div>
      

      </form>
      </div>
         
     {input && task && <Storage task = {input} des = {task} /> }
      
     
      </section>
      
    );

};

export default TaskForm;
