import TaskList from "./TaskList";

function Storage(props){

    const store = [];

    store.push({
        id : props.task,
        taskNo : props.task,
        des : props.des,
    });


  localStorage.setItem('store' , store);
 
  const getData = localStorage.getItem('store');

  console.log(...getData)
//   <TaskList  data = {des} />
};

export default Storage;
